import React from 'react';
import { View, Text, StyleSheet, ScrollView, TouchableOpacity } from "react-native";
import { Header } from "@rneui/base";
import { Icon, Avatar } from 'react-native-elements';

export default function NovoContato({ navigation }) {


  return (
    <>
      <Header
        backgroundImageStyle={{}}
        barStyle="default"
        centerComponent={{
          text: "Lista de contatos",
          style: { color: "#fff", fontWeight: "bold" }
        }}
        centerContainerStyle={{}}
        leftContainerStyle={{}}
        linearGradientProps={{}}
        placement="center"
        rightComponent={
          <TouchableOpacity onPress={() => navigation.navigate('NovoContato')} >
            <Icon name="add" color="#fff" />
          </TouchableOpacity>
        }
        rightContainerStyle={{}}
        statusBarProps={{}}
        containerStyle={{ backgroundColor: "#1670f7" }}
      />

      <ScrollView>
        <View style={styles.container} >
          {
            list.map((l, i) => (
              <button key={i} style={styles.listItem} onPress={() => navigation.navigate('EditarContato')}>
                <Avatar
                  size="medium"
                  rounded
                  source={{
                    uri: l.avatar_url,
                  }}
                />
                <Text style={styles.name}>{l.name}</Text>
              </button>
            ))
          }
        </View>
      </ScrollView>
    </>
  );
}

const styles = StyleSheet.create({
  container: {
    flexDirection: 'column',
  },
  listItem: {
    display: 'flex',
    flexDirection: 'row',
    alignItems: 'center',
    marginVertical: 10,
    border: 'none',
    padding: '10px'
  },
  name: {
    marginLeft: 10,
    fontSize: 18,
    fontWeight: 'bold',
  },
});
