import React, { useEffect, useState } from "react";
import { Text, StyleSheet, TouchableOpacity, FlatList } from "react - native";
import { Header, Icon, Avatar } from "react - native - elements";
import axios from "axios";

// Cria um componente para cada item da lista 
function ListItem({ item: contato, navigation }) {
  return (<TouchableOpacity style={styles.listItem}
    // Navega para a tela de editar contato com o id do contato 
    onPress={() => navigation.navigate("EditarContato", { id: contato.id })} > <Avatar size="medium" rounded source={{ uri: contato.avatar_url, }} /> <Text style={styles.name}>{contato.name}</Text> </TouchableOpacity>);
}

export default function ListaTell({ navigation }) {

  // Cria um estado para armazenar os contatos const 
  [contatos, setContatos] = useState([]);

  // Cria uma função para consultar os dados dos contatos 
  function consultarDados() {
    // Faz uma requisição GET para a API dos contatos 
    axios.get("http://localhost:3000/contatos").then(function (response) {
      // Atualiza o estado com os dados recebidos 
      setContatos(response.data);
    }).catch(function (error) {
      // Mostra o erro no console 
      console.log(error);
    });
  }

  // Chama a função de consultar dados quando o componente é montado 
  useEffect(() => { consultarDados(); }, []);

  return (<> <Header centerComponent={{ text: "Lista de Contatos", style: styles.headerText, }} rightComponent={<TouchableOpacity onPress={() => navigation.navigate("NovoContato")}> <Icon name="add" color="#fff" /> </TouchableOpacity >} containerStyle={styles.headerContainer} />

    <FlatList
      data={contatos}
      keyExtractor={(item) => item.id}
      renderItem={({ item }) => (
        <ListItem item={item} navigation={navigation} />
      )}
    />
  </>

  );
}

const styles = StyleSheet.create({
  container: { flexDirection: "column", }, listItem: { flexDirection: "row", alignItems: "center", padding: 15, backgroundColor: "rgba(255, 255, 255, 0.8)", borderRadius: 10, margin: 10, }, name: { marginLeft: 10, fontSize: 18, fontWeight: "bold", color: "#333", }, headerText: { color: "#fff", fontWeight: "bold", fontSize: 18, }, headerContainer: { backgroundColor: "#1670f7", },

});
