import React from 'react';
import { View, StyleSheet } from 'react-native';
import { Header, Button, Icon, Input } from 'react-native-elements';

export default function EditarContato({ navigation }) {
    const goToLogin = () => {
        navigation.navigate('ListaTell');
    };

    return (
        <View style={styles.container}>
            <Header
                backgroundImageStyle={{}}
                barStyle="default"
                centerComponent={{
                    text: "Editar Contato",
                    style: { color: "#fff", fontWeight: "bold" }
                }}
                centerContainerStyle={{}}
                leftContainerStyle={{
                    flex: 1,
                    alignItems: 'flex-start',
                    justifyContent: 'center',
                    paddingLeft: 10,
                }}
                leftComponent={
                    <Icon
                        name="arrow-left"
                        color="#fff"
                        onPress={goToLogin}
                    />
                }
                linearGradientProps={{}}
                placement="center"
                rightComponent={{}}
                rightContainerStyle={{}}
                statusBarProps={{}}
                containerStyle={{ backgroundColor: "#1670f7" }}
            />

            <View style={styles.inputContainer}>
                <Input
                    placeholder='Nome'
                    inputStyle={styles.input}
                />
                <Input
                    placeholder='Email'
                    inputStyle={styles.input}
                />
                <Input
                    placeholder='Telefone'
                    inputStyle={styles.input}
                />
            </View>

            <View style={styles.buttonContainer}>
                <Button
                    title="Alterar"
                    onPress={() => navigation.navigate('ListaTell')}
                    buttonStyle={styles.button}
                />

                <Button
                    title="Excluir"
                    onPress={() => navigation.navigate('ListaTell')}
                    buttonStyle={[styles.button, styles.deleteButton]}
                />
            </View>
        </View>
    );
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: "#fff",
    },
    inputContainer: {
        paddingHorizontal: 16,
        marginTop: 20,
    },
    input: {
        fontSize: 16,
        marginBottom: 20,
    },
    buttonContainer: {
        flexDirection: "row",
        justifyContent: "space-around",
        marginTop: 30,
    },
    button: {
        backgroundColor: "#1670f7",
        width: 120,
    },
    deleteButton: {
        backgroundColor: "red",
    },
});
