import React, { useState } from 'react';
import { View, Text, StyleSheet, ScrollView, TouchableOpacity, TextInput, ImageBackground } from "react-native";
import { Header, Icon, Button } from 'react-native-elements';

export default function NovoContato({ navigation }) {
    const [nome, setNome] = useState('');
    const [email, setEmail] = useState('');
    const [telefone, setTelefone] = useState('');

    const adicionarContato = () => {
        console.log('Nome:', nome);
        console.log('Email:', email);
        console.log('Telefone:', telefone);
        navigation.navigate('ListaTell');
    };

    return (
        <ScrollView style={styles.container}>
            <ImageBackground
                source={{ uri: 'https://placeimg.com/640/480/tech' }}
                style={styles.headerBackground}
                imageStyle={styles.headerImage}
            >
                <Header
                    leftComponent={
                        <TouchableOpacity onPress={() => navigation.navigate('ListaTell')}>
                            <Icon name="arrow-back" color="#fff" />
                        </TouchableOpacity>
                    }
                    centerComponent={{
                        text: "Adicionar Novo Contato",
                        style: { color: "#fff", fontWeight: "bold", fontSize: 20 }
                    }}
                    containerStyle={styles.headerContainer}
                />
            </ImageBackground>

            <View style={styles.formContainer}>
                <Text style={styles.label}>Nome:</Text>
                <TextInput
                    style={styles.input}
                    placeholder="Digite o nome"
                    onChangeText={text => setNome(text)}
                    value={nome}
                />

                <Text style={styles.label}>Email:</Text>
                <TextInput
                    style={styles.input}
                    placeholder="Digite o email"
                    onChangeText={text => setEmail(text)}
                    value={email}
                />

                <Text style={styles.label}>Telefone:</Text>
                <TextInput
                    style={styles.input}
                    placeholder="Digite o telefone"
                    onChangeText={text => setTelefone(text)}
                    value={telefone}
                />

                <Button
                    title="Salvar Contato"
                    onPress={adicionarContato}
                    buttonStyle={styles.addButton}
                />
            </View>
        </ScrollView>
    );
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: "#fff",
    },
    headerBackground: {
        height: 150,
    },
    headerImage: {
        opacity: 0.6,
    },
    headerContainer: {
        backgroundColor: 'transparent',
        position: 'absolute',
        top: 0,
        left: 0,
        right: 0,
    },
    formContainer: {
        flex: 1,
        padding: 20,
        marginTop: 50,
    },
    label: {
        fontSize: 18,
        fontWeight: 'bold',
        marginBottom: 5,
    },
    input: {
        height: 40,
        borderColor: '#1670f7',
        borderWidth: 1,
        borderRadius: 5,
        paddingHorizontal: 10,
        marginBottom: 20,
    },
    addButton: {
        backgroundColor: "#1670f7",
    },
});
