import React from 'react';
import { View, Text, StyleSheet } from "react-native";
import { Header } from "@rneui/base";
import { Icon } from 'react-native-elements';

export default function NovoContato({ navigation }) {
  const goToLogin = () => {
    navigation.navigate('Login');
  };

  return (
    <>
      <Header
        backgroundImageStyle={{}}
        barStyle="default"
        centerComponent={{
          text: "Novo Contato",
          style: { color: "#fff", fontWeight: "bold" }
        }}
        centerContainerStyle={{}}
        leftContainerStyle={{
          flex: 1,
          alignItems: 'flex-start',
          justifyContent: 'center',
          paddingLeft: 10,
        }}
        leftComponent={
          <Icon
            name="arrow-left"
            color="#fff"
            onPress={goToLogin} 
          />
        }
        linearGradientProps={{}}
        placement="center"
        rightComponent={{}}
        rightContainerStyle={{}}
        statusBarProps={{}}
        containerStyle={{ backgroundColor: "#1670f7" }}
      />

      <View>
      <view style={{ display: "flex", justifyContent: "center" }}>
          <Avatar size="xlarge" rounded
            source={{
              uri:
                'https://geekblog.com.br/wp-content/uploads/2020/12/Desktop-Wallpaper-Stormtrooper-Of-Star-Wars-Movie-Hd-Image-Picture-Background-8rgwwh.jpg.webp',
            }}
          />
        </view>
      </View>
    </>
  );
}

const styles = StyleSheet.create({
  header: {
    height: 50,
    backgroundColor: "#1670f7",
    borderBottomColor: "#000000",
    flexDirection: 'row',
    alignItems: "center",
    justifyContent: "space-around",
  },
  title: {
    fontSize: 20,
    fontWeight: "bold",
    color: "#ffff",
  },
});
