import * as React from 'react';
import { View, Text, StyleSheet, ImageBackground } from 'react-native';
import { Button } from 'react-native-elements';
import { getAuth, signInWithPopup, GoogleAuthProvider } from "firebase/auth";

export default function Inicio({ navigation }) {

  function LoginGoogle() {
    const provider = new GoogleAuthProvider();

    const auth = getAuth();
    signInWithPopup(auth, provider)
      .then((result) => {
        // This gives you a Google Access Token. You can use it to access the Google API.
        const credential = GoogleAuthProvider.credentialFromResult(result);
        const token = credential.accessToken;
        const user = result.user;
      }).catch((error) => {
        const errorCode = error.code;
        const errorMessage = error.message;
        const email = error.customData.email;
        const credential = GoogleAuthProvider.credentialFromError(error);
      });
  }
  return (
    <ImageBackground
      source={require('../assets/24097127_y10n_fpni_211014.jpg')}
      style={styles.backgroundImage}
    >
      <View style={styles.container}>
        <Text style={styles.welcomeText}>Bem-vindo ao Sistema</Text>
        <Button
          title="Cadastrar"
          onPress={() => navigation.navigate('Cadastrar')}
          buttonStyle={styles.button}
        />
        <Button
          title="Login"
          onPress={() => navigation.navigate('Login')}
          buttonStyle={[styles.button, styles.loginButton]}
        />
        <Button
          title="Entre com o Google"
          onPress={() => LoginGoogle()}
          buttonStyle={[styles.button, styles.googleButton]}
        />
      </View>
    </ImageBackground>
  );
}

const styles = StyleSheet.create({
  backgroundImage: {
    flex: 1,
    resizeMode: 'cover',
    justifyContent: 'center',
  },
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
    backgroundColor: 'rgba(0, 0, 0, 0.9)',
  },
  welcomeText: {
    fontSize: 24,
    fontWeight: 'bold',
    color: '#fff',
    marginBottom: 20,
  },
  button: {
    backgroundColor: '#1670f7',
    width: 200,
    marginBottom: 20,
  },
  loginButton: {
    backgroundColor: '#3CB371',
  },
  googleButton: {
    backgroundColor: '#b33c4a',
  }
});
