import React from 'react';
import { View, Text, StyleSheet, ScrollView } from "react-native";
import { Header } from "@rneui/base";
import { Icon, Avatar } from 'react-native-elements';
import { styleProps } from 'react-native-web/dist/cjs/modules/forwardedProps';

export default function NovoContato({ navigation }) {
    const goToLogin = () => {
        navigation.navigate('Login');
    };

    const list = [
        {
            name: 'Amy Farha',
            avatar_url: 'https://geekblog.com.br/wp-content/uploads/2020/12/Desktop-Wallpaper-Stormtrooper-Of-Star-Wars-Movie-Hd-Image-Picture-Background-8rgwwh.jpg.webp',
        },
        {
            name: 'Chris Jackson',
            avatar_url: 'https://geekblog.com.br/wp-content/uploads/2020/12/Desktop-Wallpaper-Stormtrooper-Of-Star-Wars-Movie-Hd-Image-Picture-Background-8rgwwh.jpg.webp',
        },
    ];

    return (
        <>
            <Header
                backgroundImageStyle={{}}
                barStyle="default"
                centerComponent={{
                    text: "Novo Contato",
                    style: { color: "#fff", fontWeight: "bold" }
                }}
                centerContainerStyle={{}}
                leftContainerStyle={{
                    flex: 1,
                    alignItems: 'flex-start',
                    justifyContent: 'center',
                    paddingLeft: 10,
                }}
                leftComponent={
                    <Icon
                        name="arrow-left"
                        color="#fff"
                        onPress={goToLogin}
                    />
                }
                linearGradientProps={{}}
                placement="center"
                rightComponent={{}}
                rightContainerStyle={{}}
                statusBarProps={{}}
                containerStyle={{ backgroundColor: "#1670f7" }}
            />

            <ScrollView>
                <View>
                    {
                        list.map((l, i) => (
                            <View key={i}>
                                <Avatar
                                    size="xlarge"
                                    rounded
                                    source={{
                                        uri: l.avatar_url,
                                    }}
                                    style={styleProps.avatar}
                                />
                                <Text style={{ textAlign: 'center' }}>{l.name}</Text>
                            </View>
                        ))
                    }
                </View>
            </ScrollView>
        </>
    );
}
